// Parallax Occlusion Mapping Shader

// Constants
float2 textureScale = float2(1.0, 1.0);
float heightScale = 0.1;
float minLayerCount = 10;
float maxLayerCount = 30;

// Sampler states
sampler2D texSampler : register(s0);
sampler2D normalSampler : register(s1);
sampler2D depthSampler : register(s2);

// Vertex shader input/output structures
struct VS_INPUT
{
    float4 position : POSITION;
    float2 texCoord : TEXCOORD0;
};

struct PS_INPUT
{
    float4 position : SV_POSITION;
    float2 texCoord : TEXCOORD0;
    float3 viewDir : TEXCOORD1;
};

// Vertex shader
PS_INPUT vs_main(VS_INPUT input)
{
    PS_INPUT output;
    output.position = mul(input.position, WorldViewProjectionMatrix);
    output.texCoord = input.texCoord * textureScale;
    output.viewDir = normalize(mul(float4(input.position.xyz, 0), ViewMatrix).xyz);
    return output;
}

// Pixel shader
float4 ps_main(PS_INPUT input) : SV_Target
{
    // Sample textures
    float4 texColor = tex2D(texSampler, input.texCoord);
    float4 normalColor = tex2D(normalSampler, input.texCoord);
    float depthValue = tex2D(depthSampler, input.texCoord).r;

    // Calculate view direction in tangent space
    float3 viewDirTS = mul(float4(input.viewDir, 0), transpose(InverseViewMatrix));

    // Initialize POM variables
    float layerCount = lerp(minLayerCount, maxLayerCount, saturate(dot(viewDirTS, float3(0, 0, 1))));
    float layerDistance = heightScale / layerCount;
    float cumulativeDepth = 0;

    // Loop through layers
    for (int i = 0; i < layerCount; i++)
    {
        // Calculate offset in tangent space
        float3 offset = viewDirTS * layerDistance;
        float2 offsetUV = input.texCoord + offset.xy;

        // Sample depth value at offset
        float depthSample = tex2D(depthSampler, offsetUV).r;

        // Check if current layer is behind previous layer
        if (depthSample > cumulativeDepth)
        {
            // Accumulate depth
            cumulativeDepth += depthSample - cumulativeDepth;
            // Add color contribution from this layer
            texColor.rgb += tex2D(texSampler, offsetUV).rgb * (1 - layerDistance * i);
        }
    }

    // Return final color
    return float4(texColor.rgb * texColor.a, texColor.a);
}
