        double sum = 0;
        for (int i = 0; i < inputs.Length; i++)
        {
            sum += inputs[i] * weights[i];
        }

        sum += weights[weights.Length - 1]; // add bias

        return Sigmoid(sum);
    }

    private double Sigmoid(double x)
    {
